import React from 'react';

import { withStyles } from '@material-ui/styles'; // jss library
import {Typography} from '@material-ui/core';
import {Link} from 'react-router-dom';
import ExternalLink from '../../../ExternalLink';

import img1 from '../../../../assets/img/blogs/messapps/simple-and-effective-ways-to-gain-app-publicity/1.jpg';

const jssStyle = theme => ({
    img: {
      display: 'block',
      marginLeft: 'auto',
      marginRight: 'auto',
      marginBottom: '30px',
      marginTop: '30px',
      maxWidth: '90%'
    },
    blockQuote: {
      background: '#f9f9f9',
      borderLeft: '10px solid #ccc',
      margin: '1.5em 10px !important',
      padding: '0.5em 10px',
      display: 'block',
      quotes: `'"''"''"''"'`,
      '&::before': {
        color: '#ccc',
        content: 'open-quote',
        fontSize: '4em',
        lineHeight: '0.1em',
        marginRight: '0.25em',
        verticalAlign: '-0.4em'
      }
    },
    center: {
        textAlign: 'center'
    }
})

const SimpleAndEffectiveWaysToGainAppPublicity = (props) => {
  const {classes} = props;
  return (
    <div>
        <img src={img1} alt={'Gaining app popularity'} className={classes.img}/>
        <Typography paragraph>The best way to get coverage for your app is by hiring a PR professional or a PR agency. A worthwhile PR Agencies charge around
        $10,000 per month and typically require businesses to keep them around for longer than one. That kind of expenditure is usually out­of­reach for the majority of both small and mid­sized businesses. If you can afford them, then you’ll be ahead of 99% of apps in the race for user acquisition:
        </Typography>
        <Typography className={classes.blockQuote} paragraph>“Working with a PR agency is extremely beneficial because they already have fantastic connections with journalists based on a mutually beneficial relationship and will know how best to publicize your company. If you are keen to hire a PR agency to help gain valuable coverage (and links) for your business, it is worth holding a PR pitch in order to get a flavor of the agencies available. Writing a robust PR brief (our guide) should help you to decide who to invite to the pitch, and it will save a lot of time and effort during the pitching process”­- <ExternalLink url={`https://www.apadmi.com/successful-apps-guide/#martinb`}>Account Director at PR Agency One Gemma Eccleston</ExternalLink>
</Typography>
    </div>
  )
}

export default withStyles(jssStyle, {withTheme: true})(SimpleAndEffectiveWaysToGainAppPublicity)
