import React from 'react';

import { withStyles } from '@material-ui/styles'; // jss library
import {Typography} from '@material-ui/core';
import {Link} from 'react-router-dom';
import ExternalLink from '../../../ExternalLink';

import img1 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/1.jpg';
import img2 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/2.jpg';
import img3 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/3.jpg';
import img4 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/4.jpg';
import img5 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/5.jpg';
import img6 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/6.jpg';
import img7 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/7.jpg';
import img8 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/8.jpg';
import img9 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/9.jpg';
import img10 from '../../../../assets/img/blogs/messapps/creating-your-first-ios-developer-account/10.jpg';

const jssStyle = theme => ({
    img: {
      display: 'block',
      marginLeft: 'auto',
      marginRight: 'auto',
      marginBottom: '30px',
      marginTop: '30px',
      maxWidth: '90%'
    },
    blockQuote: {
      background: '#f9f9f9',
      borderLeft: '10px solid #ccc',
      margin: '1.5em 10px !important',
      padding: '0.5em 10px',
      display: 'block',
      quotes: `'"''"''"''"'`,
      '&::before': {
        color: '#ccc',
        content: 'open-quote',
        fontSize: '4em',
        lineHeight: '0.1em',
        marginRight: '0.25em',
        verticalAlign: '-0.4em'
      }
    },
    center: {
        textAlign: 'center'
    }
})

const BoostYourAppsGrowthSocialImplementations = (props) => {
  const {classes} = props;
  return (
    <div>
        <Typography className={classes.center} paragraph><strong>NOTE:</strong> Creating an iOS Developer Account costs approximately $99.00 USD</Typography>
        <Typography variant='h5'>Creating an Apple Developer account is really simple</Typography>
        <Typography paragraph>What’s not, however, is creating an entire app from scratch. Typically, one's curiosity about the mystery of coding will linger, even if they plan to delegate a team to develop the app. This guide will walk you through all the steps of creating an Apple ID and Development account so that you can gain access to your very own programming control­room (Xcode).</Typography>

        <img src={img1} className={classes.img} alt='Apple Developer Agreement'/>
        
        <Typography paragraph>
        Begin by visiting apple’s website <ExternalLink url="https://developer.apple.com/enroll/">here.</ExternalLink> If you haven’t already, Create an Apple ID
or Sign­in with an existing one; you may need to re­click the URL provided above after the account creation process if you're not already reverted back to the installation page upon account creation. Next, you’ll need to choose between registering your account as an Individual Business or as a Company/Organization ­ and yes, it matters which one you select:
        </Typography>
        <ul>
            <li>
                <Typography paragraph><strong>Individual/Sole Proprietor/ Single Person Business:</strong> For individuals or companies that are not an officially recognized business; your account can only be accessed through a single login. This is most useful for freelance developers who are barely starting out or won’t need to include other users on their account.</Typography>
            </li>
        </ul>

        <Typography variant='h5'>Reward users for competing/interacting with other users to promote retention through competition:</Typography>
        <img src={img2} className={classes.img} alt='Clash of Clans screenshot'/>
        <Typography className={classes.center}><strong>Clash of Clans</strong> by Supercell</Typography>

        <Typography variant='h5'>Reward users that want to share your app:</Typography>
        <img src={img3} className={classes.img} alt='Castle Clash screenshot'/>
        <Typography className={classes.center} paragraph><strong>Castle Clash</strong> - developed by IGG.COM</Typography>

        <Typography variant='h5'>Implement social networking into your app:</Typography>
        <img src={img4} className={classes.img} alt='Miitomo screenshot'/>
        <Typography className={classes.center} paragraph><strong>Miitomo</strong> - developed by Nintendo</Typography>

        <img src={img5} className={classes.img} alt='Miitomo screenshot'/>
        <Typography className={classes.center} paragraph><strong>Clash Of Lords 2</strong> - developed by IGG.COM</Typography>

        <Typography variant='h5'>Turn multiplayer into a feature that must be unlocked:</Typography>
        <img src={img6} className={classes.img} alt='Clash Royale screenshot 1 of 2'/>
        <img src={img7} className={classes.img} alt='Clash Royale screenshot 2 of 2'/>
        <Typography className={classes.center} paragraph><strong>Clash Royale</strong> - developed by Supercell</Typography>

        <Typography variant='h5'>Incorporate friends list and private messaging functionalities:</Typography>
        <img src={img8} className={classes.img} alt='Boom Beach screenshot'/>
        <Typography className={classes.center} paragraph><strong>Boom Beach</strong> - developed by Supercell</Typography>

        <img src={img9} className={classes.img} alt='Clash of Lords 2 screenshot'/>
        <Typography className={classes.center} paragraph><strong>Clash Of Lords 2</strong> - developed by IGG.COM</Typography>

        <img src={img10} className={classes.img} alt='Castle Clash screenshot'/>
        <Typography className={classes.center} paragraph><strong>Castle Clash</strong> - developed by IGG.COM</Typography>

        <Typography variant='h5'>Provide users with an open yet moderated chat channel that users can communicate through:</Typography>
        <img src={img11} className={classes.img} alt='Castle Clash screenshot'/>
        <Typography className={classes.center} paragraph><strong>Castle Clash</strong> - developed by IGG.com</Typography>
        
        <Typography variant='h5'>Implement a guild-like system to elicit the growth of in-app communities:</Typography>
        <img src={img12} className={classes.img} alt='Clash of Lords 2 screenshot'/>
        <Typography className={classes.center} paragraph><strong>Clash of Lords 2</strong> - developed by IGG.com</Typography>

        <img src={img13} className={classes.img} alt='Boom Beach screenshot'/>
        <Typography className={classes.center} paragraph><strong>Boom Beach</strong> - developed by Supercell</Typography>
    </div>
  )
}

export default withStyles(jssStyle, {withTheme: true})(BoostYourAppsGrowthSocialImplementations)
