import React from 'react'
import { withStyles } from '@material-ui/styles'; // jss library
import {Typography, Link} from '@material-ui/core';

import davidThumbnail from '../../../assets/img/blogs/david_thumbnail.png';
import TimePeriodCaption from '../../TimePeriodCaption';

const jssStyle = theme => ({
    img: {
      display: 'block',
      marginLeft: 'auto',
      marginRight: 'auto',
      marginBottom: '15px',
      maxWidth: '90%'
    },
    blockQuote: {
      background: '#f9f9f9',
      borderLeft: '10px solid #ccc',
      margin: '1.5em 10px !important',
      padding: '0.5em 10px',
      display: 'block',
      '&::before': {
        color: '#ccc',
        content: 'open-quote',
        fontSize: '4em',
        lineHeight: '0.1em',
        marginRight: '0.25em',
        verticalAlign: '-0.4em'
      }
    },
    headingAuthorContainer: {
      display: 'inline-block',
      marginLeft: '1em',
      position: 'relative'
    },
    headingAuthor: {
      marginBottom: '-5px !important'
    }
})

/**
 * @param {string} messappsURL - url to the article's home on MessApps site
 * @param {string} date
 * @param {jsx} children - body of article
 */
const MessAppsArticle = (props) => {
    const {classes} = props;
  return (
    <div>
      <div>
      <Typography style={{float: 'right', display: 'inline'}}><a href={props.messappsURL}>Visit MessApps</a></Typography>
        <img src={davidThumbnail} style={{marginTop: '5px'}} alt="David Murphy thumbnail picture"/>
        <div className={classes.headingAuthorContainer}>
          <Typography variant="h6" className={classes.headingAuthor}>David Murphy</Typography>
          <TimePeriodCaption theme={props.theme} timeDescription={props.date}></TimePeriodCaption>
        </div>
      </div>
      <br/>
      {props.children}
    </div>
  )
}

export default MessAppsArticle
